import { useState } from "react";
import { useNavigate } from "react-router-dom";
import './login.css';

function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    function isValidEmail(email) {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Regex for basic email validation

        if (email.trim() === "") {
            setMessage("Please enter your email.");
            return false;
        }

        if (!email.includes("@")) {
            setMessage("An email address must contain '@'.");
            return false;
        }

        if (!email.includes(".")) {
            setMessage("An email address must contain a domain (e.g., '.com').");
            return false;
        }

        if (!emailRegex.test(email)) {
            setMessage("Enter a valid email address (e.g., user@example.com).");
            return false;
        }

        if (email.length > 254) {
            setMessage("Email address is too long.");
            return false;
        }

        if (email.startsWith("@") || email.endsWith("@") || email.endsWith(".")) {
            setMessage("Email address cannot start or end with special characters.");
            return false;
        }

        setMessage(""); // Clear any previous messages
        return true;
    }


    function isValidPassword(pass) {
        if (pass.trim() === "") {
            setMessage("Please enter your password.");
            return false;
        }
        if (pass.length <= 6) {
            setMessage("Password should be more than 6 characters.");
            return false;
        }
        return true;
    }

    function submit() {
        if (isValidEmail(email) && isValidPassword(password)) {
            setMessage(""); // Clear any previous messages
            navigate("/profile", { state: { email, password } }); // Redirect to Profile
        }
    }

    return (
        <>
            <div className="main">
                <div>
                    Email: 
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                </div>
                <div>
                    Password: 
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                </div>
                <div>
                    <button onClick={submit}>Login</button>
                </div>
                {message && <div className="message">{message}</div>}
            </div>
        </>
    );
}

export default Login;
